 class="col-9">                <select id="category_id" name="category_id" class="form-control">                    @foreach($categories as $category)                        <option {{ isset($product) ? ($product->category_id==$category['id'] ?'selected':'') : ( old('category_id')==$category['id'] ? 'selected':'')  }}                                value="{{$category['id']}}">{{$category['title']}}</option>                    @endforeach                </select>            </div>        </div>        @if(auth()->user()->hasRole('admin'))            <div class="form-group row ">                <label for="user" class="col-3 control-label text-right">                    انتخاب فروشنده                </label>                <div class="col-9">                    <select id="user" name="user_id" class="form-control">                        @foreach($users as $user)                            <option {{ isset($product) ? ($product->user_id==$user->id ?'selected':'') : ( old('user_id')==$user->id ? 'selected':'')  }}                                    value="{{$user->id}}">{{$user->name}}</option>                        @endforeach                    </select>                    {{--<vue-select input_name="user_id" input_label="name" :data_options="{{$users}}" data_selected=""></vue-select>--}}                </div>            </div>        @endif    </div>    <div style="padding: 0 4px;" class="col-6 pt-4">        <div class="form-group row">            <label class="col-3 control-label text-right">تصویر</label>            {{--{!! Form::label('avatar', trans("lang.user_avatar"), ['class' => 'col-3 control-label text-right']) !!}--}}            <div class="col-9">                <div style="width: 100%" class="dropzone image" id="image" data-field="image">                    <input type="hidden" name="image">                </div>                <a href="#loadMediaModal" data-dropzone="avatar" data-toggle="modal" data-target="#mediaModal"                   class="btn btn-outline-primary btn-sm float-right mt-1">انتخاب تصویر</a>                {{--<div class="form-text text-muted w-50">--}}                {{--{{ trans("lang.user_avatar_help") }}--}}                {{--</div>--}}            </div>        </div>        @prepend('scripts')        <script type="text/javascript">            var $product_image = '';            @if(isset($product) && $product->hasMedia('image'))                $product_image = {                name: "{!! $product->getFirstMedia('image')->name !!}",                size: "{!! $product->getFirstMedia('image')->size !!}",                type: "{!! $product->getFirstMedia('image')->mime_type !!}",                collection_name: "{!! $$product->getFirstMedia('image')->collection_name !!}"            }            // console.log('product_avatar', $product_image);            // alert($product_avatar);                    @endif            var dz_product_image = $(".dropzone.image").dropzone({                    url: "{!!url('/panel/uploads/store')!!}",                    addRemoveLinks: true,                    maxFiles: 2,                    init: function () {                        @if(isset($product) && $product->hasMedia('image'))                        dzInit(this, $product_image, '{!! url($product->getFirstMediaUrl('image','thumb')) !!}');                        @endif                    },                    accept: function (file, done) {                        dzAccept(file, done, this.element, "{!!config('medialibrary.icons_folder')!!}");                    },                    sending: function (file, xhr, formData) {                        dzSending(this, file, formData, '{!! csrf_token() !!}');                    },                    maxfilesexceeded: function (file) {                        dz_product_image[0].mockFile = '';                        dzMaxfile(this, file);                    },                    complete: function (file) {                        dzComplete(this, file, $product_image, dz_product_image[0].mockFile);                        dz_product_image[0].mockFile = file;                    },                    removedfile: function (file) {                        dzRemoveFile(                            file, $product_image, '{!! url("/panel/products/remove-media") !!}',                            'image', '{!! isset($product) ? $product->id : 0 !!}', '{!! url("/panel/uploads/clear") !!}', '{!! csrf_token() !!}'                        );                    }                });            dz_product_image[0].mockFile = $product_image;            dropzoneFields['image'] = dz_product_image;        </script>        @endprepend    </div>    <div style="padding: 0 4px;" class="col-12 pt-4">        <div class="form-group row ">            <div class="col-12">                <label class="control-label text-right">                    توضیحات                </label>                <textarea id="description" name="description"                          class="form-control">{!! isset($product)?($product->description):(old('description')?old('description'):'') !!}</textarea>            </div>        </div>    </div></div><!-- Submit Field --><div class="form-group col-12 text-right">    <button type="submit" class="btn btn-success"><i class="fa fa-save"></i>        ذخیره محصول    </button>    <a href="{!! route('panel.products.index') !!}" class="btn btn-light"><i class="fa fa-undo"></i>        کنسل    </a></div>@push('scripts_lib')    <script src="{{asset('ckeditor/ckeditor.js')}}"></script>    <script>        CKEDITOR.replace('description', {            filebrowserUploadUrl: '/upload-product-image',            filebrowserImageUploadUrl: '/upload-product-image'        })    </script>@endpush