<div style="flex: 50%;max-width: 50%;padding: 0 4px;" class="column pt-4">    <!-- Name Field -->    <div class="form-group row ">        <label class="col-4 control-label text-right">            عنوان ویژگی        </label>            <div class="col-8">                <input name="title" type="text"                       value="{{isset($property)?($property->title):(old('title')?old('title'):'')}}" class="form-control "                       placeholder="">            </div>    </div>    <div class="form-group row ">        <label class="col-4 control-label text-right">            عنوان ویژگی به انگلیسی        </label>        <div class="col-8">            <input name="title_en" type="text"                   value="{{isset($property)?($property->title_en):(old('title_en')?old('title_en'):'')}}"                   class="form-control "                   placeholder=""            >        </div>    </div>    <div class="form-group row ">        <label for="status" class="col-4 control-label text-right">            وضعیت ویژگی        </label>        <div class="col-8">            <select class="form-control" name="status">                <option {{ isset($property) ? ($property->status==1?'selected':'') : (old('status')==1?'selected':'')}} value="1">                    اعمال شده                </option>                <option {{isset($property)? ($property->status==0?'selected':'') : (old('status')==0?'selected':'') }} value="0">                    مخفی                </option>            </select>        </div>    </div>    <div class="form-group row ">        <label for="showBanner" class="col-4 control-label text-right">            نمایش در بنر محصول        </label>        <div class="col-8">            <select id="showBanner" class="form-control" name="showBanner">                <option {{isset($property) ? ($property->showBanner==1?'selected':'') : (old('showBanner')==1?'selected':'') }} value="1">                